@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ClientScreen(clients: List<Client>, onAddClient: () -> Unit,onClientClick:(Client)->Unit) {
    // Box to position floating action button at the bottom right corner
    Box(
        modifier = Modifier
            .fillMaxSize()

            .background(Primary)
    ) {
        Column(modifier = Modifier.fillMaxSize()) {
            // Heading above the LazyColumn
            Text(
                text = "Clients",  // Heading text
                style = CustomTypography.titleLarge.copy(

                    fontSize = 40.sp,
                    fontWeight = FontWeight.Bold,
                    color = Color.White
                ),
                modifier = Modifier
                    .padding(vertical = 16.dp)
                    .fillMaxWidth(),
                textAlign = TextAlign.Center // Center-align the heading
            )

            // List of clients
            LazyColumn(
                modifier = Modifier.fillMaxSize(),
                verticalArrangement = Arrangement.spacedBy(0.2.dp)
            ) {
                items(clients) { client ->
                    ClientItem(client = client,onClientClick)
                }
            }
        }

        // Floating action button at the bottom right corner
        FloatingActionButton(
            onClick = onAddClient,
            modifier = Modifier
                .align(Alignment.BottomEnd)
                .padding(16.dp),
            containerColor = Color.White,
            shape = CircleShape
        ) {
            Icon(
                painter = painterResource(id = R.drawable.ic_add), // Use appropriate add icon
                contentDescription = "Add Client",
                tint = Primary
            )
        }
    }
}

// Helper function to convert Base64 to Bitmap
fun base64ToBitmap(base64String: String): Bitmap? {
    return try {
        val decodedString = Base64.decode(base64String, Base64.DEFAULT)
        BitmapFactory.decodeByteArray(decodedString, 0, decodedString.size)
    } catch (e: Exception) {
        e.printStackTrace()
        null
    }
}

@Composable
fun ClientItem(client: Client, onClick: (Client) -> Unit) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(8.dp)
            .clip(RoundedCornerShape(12.dp))
            .background(Color.LightGray)
            .clickable { onClick(client) } // Handle click event
            .padding(8.dp),
        verticalAlignment = Alignment.CenterVertically
    ) {
        val bitmap = base64ToBitmap(client.imageBase64)
        bitmap?.let {
            Image(
                bitmap = it.asImageBitmap(),
                contentDescription = client.name,
                modifier = Modifier
                    .size(56.dp)
                    .clip(CircleShape),
            )
        }

        Spacer(modifier = Modifier.width(8.dp))

        Text(
            text = client.name,
            style = MaterialTheme.typography.bodyLarge.copy(
                fontWeight = FontWeight.Bold,
                fontSize = 16.sp
            ),
            color = Color.Black
        )
    }
}

